import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute } from '@angular/router';
import { ApiService } from '../../services/api.service';
@Component({
  standalone: true, imports:[CommonModule], selector:'app-resultados',
  templateUrl:'./resultados.component.html', styleUrls:['./resultados.component.css']
}) export class ResultadosComponent implements OnInit {
  kpis:any=null; loading=true; error='';
  constructor(private route: ActivatedRoute, private api: ApiService){}
  async ngOnInit(){ const runId = this.route.snapshot.queryParamMap.get('runId') ?? 'demo-run-001';
    const res = await this.api.results(runId); this.loading=false;
    if(!res.ok){ this.error = res.error ?? 'No hay resultados'; return; }
    this.kpis = res.data!.kpis; }
}
<h1>Resultados</h1>
<div *ngIf="loading">Cargando…</div>
<div *ngIf="error" class="error">{{error}}</div>
<section *ngIf="kpis" class="kpis">
  <div class="kpi"><h3>LCOE</h3><p>{{kpis.lcoe | number:'1.3-3'}} $/kWh</p></div>
  <div class="kpi"><h3>Atendida</h3><p>{{kpis.served*100 | number:'1.1-1'}} %</p></div>
  <div class="kpi"><h3>NSE</h3><p>{{kpis.nse*100 | number:'1.1-1'}} %</p></div>
  <div class="kpi"><h3>Potencia Inst.</h3><p>{{kpis.p_inst}} kW</p></div>
</section>
<section class="grid two">
  <article class="card"><h4>Mix</h4><img src="/assets/wireframes/wf1-udea-classic/mix.png" alt="Mix"></article>
  <article class="card"><h4>Potencias</h4><img src="/assets/wireframes/wf1-udea-classic/potencias.png" alt="Potencias"></article>
  <article class="card wide"><h4>Series</h4><img src="/assets/wireframes/wf1-udea-classic/series.png" alt="Series"></article>
</section>
