import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import { DataService, FileKind } from '../../services/data.service';
@Component({
  standalone: true, imports:[CommonModule], selector:'app-cargar-datos',
  templateUrl:'./cargar-datos.component.html', styleUrls:['./cargar-datos.component.css']
}) export class CargarDatosComponent {
  status: Record<FileKind,'pending'|'ok'|'error'> = { demand:'pending', forecast:'pending', parameters:'pending', instance:'pending' };
  errorMsg = '';
  constructor(public data: DataService, private router: Router){}
  async onFile(kind: FileKind, file: File|null){ this.data.setFile(kind,file); this.status[kind]='pending'; if(!file) return;
    try{ const txt=await this.data.readText(file);
      if(kind==='demand') await this.data.validateDemand(txt);
      else if(kind==='forecast') await this.data.validateForecast(txt);
      else if(kind==='parameters') this.data.validateParameters(JSON.parse(txt));
      else if(kind==='instance') this.data.validateInstance(JSON.parse(txt));
      this.status[kind]='ok'; } catch(e:any){ this.status[kind]='error'; this.errorMsg=e?.message ?? 'Error'; } }
  continuar(){ if(this.data.allReady()) this.router.navigateByUrl('/configuracion'); }
}
<h1>Cargar datos</h1>
<div class="grid two">
  <div class="card">
    <label>Demand CSV <input type="file" accept=".csv" (change)="onFile('demand', $event.target.files[0])"></label>
    <p class="filename" *ngIf="data.files['demand']">{{ data.files['demand']!.name }}</p>
  </div>
  <div class="card">
    <label>Forecast CSV <input type="file" accept=".csv" (change)="onFile('forecast', $event.target.files[0])"></label>
    <p class="filename" *ngIf="data.files['forecast']">{{ data.files['forecast']!.name }}</p>
  </div>
  <div class="card">
    <label>Parameters JSON <input type="file" accept=".json" (change)="onFile('parameters', $event.target.files[0])"></label>
    <p class="filename" *ngIf="data.files['parameters']">{{ data.files['parameters']!.name }}</p>
  </div>
  <div class="card">
    <label>Instance JSON <input type="file" accept=".json" (change)="onFile('instance', $event.target.files[0])"></label>
    <p class="filename" *ngIf="data.files['instance']">{{ data.files['instance']!.name }}</p>
  </div>
</div>
<div class="status">
  <p [class.ok]="status.demand==='ok'" [class.error]="status.demand==='error'">Demand: {{status.demand}}</p>
  <p [class.ok]="status.forecast==='ok'" [class.error]="status.forecast==='error'">Forecast: {{status.forecast}}</p>
  <p [class.ok]="status.parameters==='ok'" [class.error]="status.parameters==='error'">Parameters: {{status.parameters}}</p>
  <p [class.ok]="status.instance==='ok'" [class.error]="status.instance==='error'">Instance: {{status.instance}}</p>
</div>
<div class="actions">
  <button class="btn" [disabled]="!data.allReady()" (click)="continuar()">Continuar → Configuración</button>
  <span *ngIf="errorMsg" class="error">{{errorMsg}}</span>
</div>
